mapper.select:


SELECT * FROM reservation 
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT * FROM reservation WHERE 1 = 1

     AND user_id = #{userId}


     AND parking_space_id = #{parkingSpaceId}


     AND parking_id = #{parkingId}


     AND status = #{status}


     AND reservation_no = #{reservationNo}


     AND vehicle_info LIKE CONCAT('%', #{vehicleInfo}, '%')


     AND start_time >= #{startTimeFrom}


     AND start_time <= #{startTimeTo}


     AND end_time >= #{endTimeFrom}


     AND end_time <= #{endTimeTo}

 ORDER BY created_at DESC


SELECT COUNT(*) FROM reservation 
WHERE parking_space_id = #{parkingSpaceId}
AND status = 0


     AND id != #{excludeId}


AND (
    (start_time < #{endTime} AND end_time > #{startTime})
)


SELECT COUNT(*) FROM reservation 
WHERE parking_space_id = #{parkingSpaceId}
AND status = 0
AND id != #{excludeId}
AND (
    (start_time < #{endTime} AND end_time > #{startTime})
)
